mapper.select:
SELECT count(*)
FROM (

	select SEQ from TB_RECEIPT_STAMP where SHOP_INFO_NO = (SELECT SHOP_INFO_NO FROM TB_RECEIPT_SHOP WHERE SHOP_BIZNO = #{BIZNO} AND SHOP_INFO_NO = #{shop_info_no}) 

		and STAMP_COMP_DTM between DATE_FORMAT(STR_TO_DATE(${startDate},'%Y%m%d'),'%Y-%m-%d 00:00:00') and DATE_FORMAT(STR_TO_DATE(${endDate},'%Y%m%d'),'%Y-%m-%d 23:59:59') 


		and STAMP_USE_YN = #{selectUse}


	UNION ALL

	select SEQ from TB_MANUAL_STAMP where SHOP_INFO_NO = (SELECT SHOP_INFO_NO FROM TB_RECEIPT_SHOP WHERE SHOP_BIZNO = #{BIZNO} AND SHOP_INFO_NO = #{shop_info_no}) 

		and STAMP_COMP_DTM between DATE_FORMAT(STR_TO_DATE(${startDate},'%Y%m%d'),'%Y-%m-%d 00:00:00') and DATE_FORMAT(STR_TO_DATE(${endDate},'%Y%m%d'),'%Y-%m-%d 23:59:59') 


		and STAMP_USE_YN = #{selectUse}


) a

select DATE_FORMAT(CARWASHUSETIME, '%Y-%m-%d %H:%i:%S') AS CARWASHUSETIME, DATE_FORMAT(FRT_CREA_DTM, '%Y-%m-%d %H:%i:%S') AS FRT_CREA_DTM, SEQ, SALES_TOT_AMT, CARWASHYN 
from TB_RECEIPT_ISSUE where SHOP_BIZNO = #{shop_bizno} 

	and FRT_CREA_DTM between DATE_FORMAT(STR_TO_DATE(${startDate},'%Y%m%d'),'%Y-%m-%d 00:00:00') and DATE_FORMAT(STR_TO_DATE(${endDate},'%Y%m%d'),'%Y-%m-%d 23:59:59') 


	and CARWASHYN = #{selectUse}

SELECT * FROM( SELECT SEQ,STAMP_CODE,STAMP_COMP_DTM, STAMP_END_DTM, STAMP_CNT,STAMP_USE_YN , USER_KEY , "rs" as STAMP_TYPE FROM TB_RECEIPT_STAMP WHERE SHOP_INFO_NO IN (SELECT SHOP_INFO_NO FROM TB_RECEIPT_SHOP WHERE SHOP_BIZNO = #{BIZNO} AND SHOP_INFO_NO = #{shop_info_no})
and STAMP_COMP_DTM between DATE_FORMAT(STR_TO_DATE(${startDate},'%Y%m%d'),'%Y-%m-%d 00:00:00') and DATE_FORMAT(STR_TO_DATE(${endDate},'%Y%m%d'),'%Y-%m-%d 23:59:59')
and STAMP_USE_YN = #{selectUse}
UNION ALL
SELECT SEQ, STAMP_CODE,STAMP_COMP_DTM, STAMP_END_DTM, STAMP_CNT,STAMP_USE_YN , USER_PHONE , "ms" as STAMP_TYPE FROM TB_MANUAL_STAMP WHERE SHOP_INFO_NO IN (SELECT SHOP_INFO_NO FROM TB_RECEIPT_SHOP WHERE SHOP_BIZNO = #{BIZNO} AND SHOP_INFO_NO = #{shop_info_no})
and STAMP_COMP_DTM between DATE_FORMAT(STR_TO_DATE(${startDate},'%Y%m%d'),'%Y-%m-%d 00:00:00') and DATE_FORMAT(STR_TO_DATE(${endDate},'%Y%m%d'),'%Y-%m-%d 23:59:59')
and STAMP_USE_YN = #{selectUse}
) a order by STAMP_COMP_DTM desc LIMIT #{firstpage} , 10

SELECT * FROM( SELECT SEQ,STAMP_CODE,STAMP_COMP_DTM, STAMP_END_DTM, STAMP_CNT,STAMP_USE_YN , USER_KEY FROM TB_RECEIPT_STAMP WHERE SHOP_INFO_NO IN (SELECT SHOP_INFO_NO FROM TB_RECEIPT_SHOP WHERE SHOP_BIZNO = #{BIZNO} AND SHOP_INFO_NO = #{SHOP_INFO_NO})
and DATE(NOW()) <= DATE(STAMP_END_DTM)
and STAMP_USE_YN = 'N' AND USER_KEY = #{USER_KEY}
UNION ALL
SELECT SEQ, STAMP_CODE,STAMP_COMP_DTM, STAMP_END_DTM, STAMP_CNT,STAMP_USE_YN , USER_PHONE FROM TB_MANUAL_STAMP WHERE SHOP_INFO_NO IN (SELECT SHOP_INFO_NO FROM TB_RECEIPT_SHOP WHERE SHOP_BIZNO = #{BIZNO} AND SHOP_INFO_NO = #{SHOP_INFO_NO})
and DATE(NOW()) <= DATE(STAMP_END_DTM)
and STAMP_USE_YN = 'N' AND USER_PHONE = #{USER_KEY}
) a
